import React, { useState, useEffect } from "react";
import axios from "axios";
let ProductSidebar = ({ sendData }) => {
  let [filterProduct, setFilterProduct] = useState({
    products: [],
  });
  let { products } = filterProduct;

  useEffect(() => {
    let dataUrl = `https://fakestoreapi.com/products`;
    axios
      .get(dataUrl)
      .then((response) => {
        setFilterProduct({
          products: response.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  let getUniqueData = (data, property) => {
    let newData = data.map((element) => {
      return element[property];
    });
    return (newData = ["All", ...new Set(newData)]);
  };

  let categoryData = getUniqueData(products, "category");

  let handleCategoryFilter = (cateData) => {
    const result = products.filter((element) => {
      return element.category === cateData;
    });
    sendData(result);
  };

  return (
    <React.Fragment>
      {/* <>{JSON.stringify(products)}</> */}
      <section className="pt-3">
        <div className="filter-category">
          <h4>Category</h4>
          {/* <button className="btn p-0 mb-1"
            onClick={handleAll}
          >All</button>
          <br /> */}
          {categoryData.map((data, index) => {
            return (
              <>
                {/* <button
                  key={index}
                  className="btn p-0 mb-1"
                  onClick={handleCategoryFilter.bind(this, data)}
                >
                  {data}
                </button>
                <br /> */}
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    value=""
                    id="flexCheckDefault"
                  />
                  <label className="form-check-label" for={data}>
                   {data}
                  </label>
                </div>
              </>
            );
          })}

          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              value=""
              id="flexCheckDefault"
            />
            <label className="form-check-label" for="flexCheckDefault">
              Default checkbox
            </label>
          </div>
        </div>
      </section>
    </React.Fragment>
  );
};
export default ProductSidebar;
