import React, { useState } from "react";

let ProductSidebar = ({ allProduct,sendData }) => {

    let filterData=[allProduct]


  let getUniqueData = (data, property) => {
    let newData = data.map((element) => {
      return element[property];
    });
    return (newData = ["All", ...new Set(newData)]);
  };

  let categoryData = getUniqueData(allProduct, "category");

  let handleCategoryFilter=(cateData)=>{
    const result=allProduct.filter(element=>{
        return element.category === cateData
    })
    sendData(result)

  }
  return (
    <React.Fragment>
      <section className="pt-3">
      <div className="filter-category">
        <h4>Category</h4>
          {categoryData.map((data,index) => {
            return <div key={index}><button className="btn p-0 mb-1" onClick={handleCategoryFilter.bind(this, data)}>{data}</button> <br/></div>
          })}
      </div>
      </section>
    </React.Fragment>
  );
};
export default ProductSidebar;
