import React, { useState } from "react";

let ProductSidebar = ({ allProduct }) => {
    let[filterProduct, setFilterProduct]=useState({
        filterData:allProduct
    })

  let getUniqueData = (data, property) => {
    let newData = data.map((element) => {
      return element[property];
    });
    return (newData = ["All", ...new Set(newData)]);
  };

  let categoryData = getUniqueData(allProduct, "category");

  let handleCategoryFilter=(cateData)=>{
    const result=allProduct.filter(element=>{
        return element.category=== cateData
    })
    setFilterProduct(result)
  }
  return (
    <React.Fragment>
      <section className="pt-3">

      <pre>{JSON.stringify(filterProduct)}</pre>
      </section>
    </React.Fragment>
  );
};
export default ProductSidebar;
