import axios from "axios";
import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import ProductSidebar from "./ProductSidebar";

let Products = () => {
  const [productState, setProductState] = useState({
    products: [],
  });
  const [selectedCategory, setSelectedCategory] = useState();

  useEffect(() => {
    let dataUrl = `https://fakestoreapi.com/products`;
    axios
      .get(dataUrl)
      .then((response) => {
        setProductState({
          products: response.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
 let handleCategoryChange=()=>{
  if (!selectedCategory) {
    return productState;
  }
  return productState.filter((item) => item.category === selectedCategory);

 }
  let { products } = productState;
  return (
    <React.Fragment>
      <div className="container">
        <div className="row">
          <div className="col-lg-3">
            <ProductSidebar />
            <select
              name="category-list"
              id="category-list"
              onChange={handleCategoryChange}
            >
              <option value="">All</option>
              <option value="Outdoor">Outdoor</option>
              <option value="Indoor">Indoor</option>
              <option value="Aquatics">Aquatics</option>
            </select>
          </div>
          <div className="col-lg-9 pt-3">
            <div className="row">
              {products.map((product) => {
                return (
                  <div className="col-md-3" key={product.id}>
                    <div className="card product-card mb-4">
                      <NavLink to={`/products/${product.id}`}>
                        <img
                          className="card-img-top img-fluid "
                          src={product.image}
                          alt={product.title}
                        />
                        <div className="card-body text-black text-decoration-none">
                          <h6>{product.title}</h6>
                          <p className="card-text mb-0">
                            â‚¹{Math.round(product.price)}
                          </p>
                          <p className="mb-0">
                            Rating: {product.rating.rate}/5
                          </p>
                          <p>Category: {product.category}</p>
                        </div>
                      </NavLink>

                      <button className="btn btn-sm btn-warning ">
                        Add to cart
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};
export default Products;
