import React, { useState, useEffect } from "react";
import axios from "axios";
let ProductSidebar = ({ sendData }) => {
  let [filterProduct, setFilterProduct] = useState({
    products: [],
  });
  let { products } = filterProduct;

  useEffect(() => {
    let dataUrl = `https://fakestoreapi.com/products`;
    axios
      .get(dataUrl)
      .then((response) => {
        setFilterProduct({
          products: response.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  let getUniqueData = (data, property) => {
    let newData = data.map((element) => {
      return element[property];
    });
    return (newData = [...new Set(newData)]);
  };

  let categoryData = getUniqueData(products, "category");

  let handleCategoryFilter = (cateData) => {
    const result = products.filter((element) => {
      return element.category === cateData;
    });
    sendData(result);
  };



//sorting
sorting = (e) => {
    const sorting = e.target.value;

    const sortRes = this.state.products.sort((a, b) => {
      if (sorting === "all") {
        return a.id > b.id ? 1 : -1;
      }

      if (sorting === "low") {
        return a.price > b.price ? 1 : -1;
      }

      if (sorting === "high") {
        return a.price < b.price ? 1 : -1;
      }
    });

    this.setState(
      {
        sort: sorting,
        products: sortRes
      },
      () => {
        localStorage.setItem("sort", JSON.stringify(this.state.sort));
        localStorage.setItem("data", JSON.stringify(this.state.products));
      }
    );
  };


  return (
    <React.Fragment>
      <section className="pt-3">
        <div className="filter-category">
          <h4>Category</h4>
          <button className="btn p-0 mb-1" onClick={() => sendData(products)}>
            All
          </button>
          <br />
          {categoryData.map((data, index) => {
            return (
              <>
                <button
                  key={index}
                  className="btn p-0 mb-1"
                  onClick={handleCategoryFilter.bind(this, data)}
                >
                  {data}
                </button>
                <br />
              </>
            );
          })}
        </div>

        <div className="filter-Rating mt-5">
          <h4>Rating</h4>
          <select className="form-select">
            <option selected>Select Rating</option>
            <option value="1">Below 5</option>
            <option value="2">Below 4</option>
            <option value="3">Below 3</option>
            <option value="3">Below 2</option>
            <option value="3">Below 1</option>
          </select>
        </div>

        <div className="filter-price mt-5">
          <h4>Price</h4>
          <select className="form-select">
            <option selected>Select Price...</option>
            <option value="1">High to Low</option>
            <option value="2">Low to High</option>
          </select>
        </div>
      </section>
    </React.Fragment>
  );
};
export default ProductSidebar;
