import React, { useEffect, useState } from "react";
import { useParams} from 'react-router-dom';
import axios from 'axios';
import ImageGallery from 'react-image-gallery';




let ProductDetail=()=>{
    let{productId}=useParams();
    const[selectProduct, setSelectProduct]=useState({
    })
    useEffect(() => {
        let dataUrl = `https://fakestoreapi.com/products/${productId}`;

        axios.get(dataUrl).then((response) => {
            setSelectProduct(response.data
            )
        }).catch((error) => {
            console.error(error);
        });
    }, []);

    const gallery = [
        {
          original: 'https://picsum.photos/id/1018/1000/600/',
          thumbnail: 'https://picsum.photos/id/1018/250/150/',
        },
        {
          original: 'https://picsum.photos/id/1015/1000/600/',
          thumbnail: 'https://picsum.photos/id/1015/250/150/',
        },
        {
          original: 'https://picsum.photos/id/1019/1000/600/',
          thumbnail: 'https://picsum.photos/id/1019/250/150/',
        },
      ];
    return(
        <React.Fragment>
            <div className="container pt-4">
                <div className="row">
                    <div className="col-md-3">
                        <div className="product-img">
                        <ImageGallery items={gallery} />
                            {/* <img src={selectProduct.image} className="img-fluid" /> */}
                        </div>
                    </div>
                    <div className="col-md-9">
                        <div className="product-detail p-4">
                            <h4>{selectProduct.title}</h4>
                            <h2> â‚¹{Math.round(selectProduct.price)}.00</h2>
                            <p>{selectProduct.description}</p>
                            <p>Category: {selectProduct.category}</p>
                            <button className="btn btn-success mx-2">Buy Now</button>
                            <button className="btn btn-warning">Add to cart</button>
                        </div>
                    </div>
                </div>
            </div>

        </React.Fragment>
    )
}
 export default ProductDetail
