import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Navbar from "../src/components/layout/NavBar";
import Products from "../src/components/products/Products";
import ProductDetail from "../src/components/products/ProductDetail";
import Cart from "../src/components/products/Cart";
import { useState } from 'react';
function App() {
  const [selectProduct, setSelectProduct]= useState([])
  let getData=(productData)=>{
    setSelectProduct(productData)
  }
  return (
    <div>
       <BrowserRouter>
        <Navbar cartUpdate={selectProduct} />
        <Routes>
          <Route exact path="/" element={<Products productData={getData}/>} />
          <Route exact path="/products/:productId" element={<ProductDetail/>}/>
          <Route exact path="/cart" element={<Cart/>} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
