import axios from "axios";
import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import ProductSidebar from "./ProductSidebar";

let Products = () => {
  const [productState, setProductState] = useState({
    products: [],
    filterProducts:[]
  });  

  let { products } = productState;


  useEffect(() => {
    let dataUrl = `https://fakestoreapi.com/products`;
    axios
      .get(dataUrl)
      .then((response) => {
        setProductState({
          products,filterProducts: response.data,

        });
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);


  let getUniqueData = (data, property) => {
    let newData = data.map((element) => {
      return element[property];
    });
    return (newData = ["All", ...new Set(newData)]);
  };

  let categoryData = getUniqueData(products, "category");

  let handleCategoryFilter=(cateData)=>{
    const result=products.filter(element=>{
        return element.category=== cateData
    })
    setProductState({
      products: result
    })
  }

  return (
    <React.Fragment>
      <div className="container">
        <div className="row">
          <div className="col-lg-3">
            <ProductSidebar 

            />
              <div className="filter-category">
        <h4>Category</h4>
          {categoryData.map((data,index) => {
            return <div key={index}><button className="btn p-0 mb-1" onClick={handleCategoryFilter.bind(this, data)}>{data}</button> <br/></div>
          })}
      </div>
          </div>
          <div className="col-lg-9 pt-3">
            <div className="row">
              {products.map((product) => {
                return (
                  <div className="col-md-3" key={product.id}>
                    <div className="card product-card mb-4">
                      <NavLink to={`/products/${product.id}`}>
                        <img
                          className="card-img-top img-fluid "
                          src={product.image}
                          alt={product.title}
                        />
                        <div className="card-body text-black text-decoration-none">
                          <h6>{product.title}</h6>
                          <p className="card-text mb-0">
                            â‚¹{Math.round(product.price)}
                          </p>
                          <p className="mb-0">
                            Rating: {product.rating.rate}/5
                          </p>
                          <p>Category: {product.category}</p>
                        </div>
                      </NavLink>

                      <button className="btn btn-sm btn-warning ">
                        Add to cart
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};
export default Products;
