import axios from "axios";
import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import ProductSidebar from "./ProductSidebar";

let Products = () => {
  const [productState, setProductState] = useState({
    products: [],
  });

  let { products } = productState;

  useEffect(() => {
    let dataUrl = `https://fakestoreapi.com/products`;
    axios
      .get(dataUrl)
      .then((response) => {
        setProductState({
          products: response.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const receiveData = (sendData) => {
    setProductState({
      products:sendData
    });
  };

  return (
    <React.Fragment>
      <pre>{JSON.stringify(productState)}</pre>
      <div className="container">
        <div className="row">
          <div className="col-lg-3">
            <ProductSidebar allProduct={products} sendData={receiveData} />
          </div>
          <div className="col-lg-9 pt-3">
            <div className="row">
              {products.length > 0 &&
              products.map((product) => {
                return (
                  <div className="col-md-3" key={product.id}>
                    <div className="card product-card mb-4">
                      <NavLink to={`/products/${product.id}`}>
                        <img
                          className="card-img-top img-fluid "
                          src={product.image}
                          alt={product.title}
                        />
                        <div className="card-body text-black text-decoration-none">
                          <h6>{product.title}</h6>
                          <p className="card-text mb-0">
                            â‚¹{Math.round(product.price)}
                          </p>
                          <p className="mb-0">
                            Rating: {product.rating.rate}/5
                          </p>
                          <p>Category: {product.category}</p>
                        </div>
                      </NavLink>

                      <button className="btn btn-sm btn-warning ">
                        Add to cart
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};
export default Products;
